import { getDimensions } from "../hooks/getDimensions";
import "../styles/pixelGrid.css";
import getMousePos from "../hooks/getMousePosition";

export default function PixelGrid() {
    const dimensions = getDimensions();
    let canvasDim = {x: dimensions.width / 2, y: dimensions.height / 2};

    const mousePos = getMousePos();
    let gridPoints = convertPositionToGrid(mousePos, canvasDim, 4);

    return (
        <div className="centered">
            <p>Window's dimensions are {dimensions.width} by {dimensions.height}</p>
            <p>Mouse was last seen on canvas at point: ({mousePos.x} , {mousePos.y})</p>
            <p>Last active gridpoint based on position: ({gridPoints.x} , {gridPoints.y})</p>
            <canvas className="pixelGrid" id="pixelGrid" width={canvasDim.x} height={canvasDim.y}></canvas>
        </div>
    );
}

function convertPositionToGrid(pos, dimensions, size) {
    let scale = dimensions.x / size * 2;

    return {
        x: convert(pos.x, scale),
        y: convert(pos.y, scale)
    };
}

function convert(val, scale) {
    return Math.ceil(val / scale);
}
