import { getDimensions } from "../hooks/getDimensions";
import "../styles/pixelGrid.css";

export default function PixelGrid() {
    const dimensions = getDimensions();

    let canvasDim = {w: dimensions.width / 2, h: dimensions.height / 2};
    return (
        <div>
            <p>Window's dimensions are {dimensions.width} by {dimensions.height}</p>
            <canvas className="pixelGrid" id="pixelGrid" width={canvasDim.w} height={canvasDim.h}></canvas>
        </div>
    );
}
