import { useState , useEffect } from 'react';
import { loadGetInitialProps } from 'next/dist/next-server/lib/utils';

export default function getMousePos() {
    const [position, setPosition] = useState({x: 0, y: 0});

    useEffect(() => {}, []);

    return position;
}
